const NumberSelector = ({dishNum, setDishNum}) => {

	const onClick_add = () => {
		setDishNum(dishNum + 1)
	}

	const onClick_minus = () => {
		if (dishNum > 0)
			setDishNum(dishNum - 1)
	}

	return (
		<div className="inline-block flex space-x-4">
			<span onClick={onClick_minus}>-</span>
			<span>{dishNum}</span>
			<span onClick={onClick_add}>+</span>
		</div>

	)
}

export default NumberSelector;
