import * as React from "react"
import Card from '@mui/material/Card';
import CardContent from '@mui/material/CardContent';
import CardMedia from '@mui/material/CardMedia';
import Button from '@mui/material/Button';
import Typography from '@mui/material/Typography';
import Backdrop from '@mui/material/Backdrop';
import Box from '@mui/material/Box';
import Modal from '@mui/material/Modal';
import Fade from '@mui/material/Fade';
import Divider from '@mui/material/Divider';
import { TextField } from "@mui/material";

const Dish = ({dish}) => {
	const [modalOpen, setModalOpen] = React.useState(false)
	const onClick_open = () => setModalOpen(true)
	const onClick_close = () => setModalOpen(false)
	
	const modalStyle = {
		position: 'absolute',
		top: '40%',
		left: '50%',
		transform: 'translate(-50%, -50%)',
		width: "80%",
		maxWidth: 400,
		bgcolor: 'background.paper',
		border: '2px solid #000',
		boxShadow: 24,
		p: 4,
		textAlign:"center",
	}

	return (
		<div>
			<Card sx={{ maxWidth:250 }} className="border-0 hover:border-0 border-gray-200">
			<Button onClick={onClick_open}>
			<div className="bg-slate-300 w-30">
				<CardMedia
					className="h-16 w-20"
					component="img"
					alt="圖片尚未準備"
					image={dish.image}
				/>
				<CardContent>
					<Typography gutterBottom variant="body1" component="div">
					{dish.name}
					</Typography>
					<Typography variant="body2" color="text.secondary">
					$ {dish.price}
					</Typography>
				</CardContent>
				</div>
				</Button>
					<Divider variant="middle" />
				<Button size="small" className="justify-self-end">加到購物車</Button>
			</Card>
			<div className="w-0">
				<Modal
				// className="modal-size" 
				aria-labelledby="transition-modal-title"
				aria-describedby="transition-modal-description"
				open={modalOpen}
				onClose={onClick_close}
				closeAfterTransition
				BackdropComponent={Backdrop}
				BackdropProps={{
					timeout: 300,
				}}
				>
					<Fade in={modalOpen}>
						<Box sx={modalStyle}>
							<img src={dish.image} className={"pb-4 w-full max-w-md h-44 sm:h-52"}/ >
							<Typography id="transition-modal-title" variant="h5" component="h2">
								<p className="text-sky-500">{dish.name}</p>
							</Typography>
							<Typography id="transition-modal-description" sx={{ mt: 0.5 }}>
								<p className="text-xs">{dish.description}</p>
								<p className="pt-4 text-lg">$ {dish.price}</p>
							</Typography>
							<TextField
								id="outlined-textarea"
								label="備註欄"
								placeholder="若您有特殊需求，像是火鍋不要芋頭或是湯裡香菜加倍，可於此備註"
								multiline
							/>
						</Box>
					</Fade>
				</Modal>
			</div>
		</div>
	)
}


export default Dish;
