import AnimateButton from "../Components/AnimateButton";
import { useState, useEffect} from "react";


const dishes = [
	{title:"義大利麵", image:"./1.jpg", type:"主食"}

]



const Menu = () =>{

	const [dishType, setDishType] = useState("")
	// const [allType, setAllType] = useState([])
	const [allTypeName, setAllTypeName] = useState([])
	const [allTypeImage, setAllTypeImage] = useState([])

	useEffect(() => {
		for (let i = 0; i < dishes.length; i++){
			console.log(allTypeName.includes(dishes[i].type))
			if(allTypeName.includes(dishes[i].type) === false){
				setAllTypeName([...allTypeName, dishes[i].type])
				setAllTypeImage([...allTypeImage, dishes[i].image])
			}
		}
		console.log(allTypeName)		
	}, []);

	const onClick_dishType = (e, type) => {
		console.log(type)
		setDishType(type)
	}

	return (
		<>
		{
		dishType === ""
		?
		allTypeName.map(
			({dish, index})=><div onClick={e => onClick_dishType(e, dish.type)}><p>{index}</p><AnimateButton imageUrl={allTypeImage[index]} imageTitle={dish.title} imageWidth={"30%"}></AnimateButton></div>) 
		:
		<p>nothing</p>
		}

		</>
	)	
}

export default Menu;
