import * as React from "react"
import Card from '@mui/material/Card';
import CardActions from '@mui/material/CardActions';
import CardContent from '@mui/material/CardContent';
import CardMedia from '@mui/material/CardMedia';
import Button from '@mui/material/Button';
import Typography from '@mui/material/Typography';
import Backdrop from '@mui/material/Backdrop';
import Box from '@mui/material/Box';
import Modal from '@mui/material/Modal';
import Fade from '@mui/material/Fade';
import Divider from '@mui/material/Divider';



const Dish = ({dish}) => {
	const [modalOpen, setModalOpen] = React.useState(false)
	const onClick_open = () => setModalOpen(true)
	const onClick_close = () => setModalOpen(false)
	
	const modalStyle = {
		position: 'absolute',
		top: '50%',
		left: '50%',
		transform: 'translate(-50%, -50%)',
		width: 400,
		bgcolor: 'background.paper',
		border: '2px solid #000',
		boxShadow: 24,
		p: 4,
	}

	return (
		<div>
			<Card sx={{ maxWidth:250 }} className="border-0 hover:border-2 border-gray-200">
			<Button onClick={onClick_open}>
			<CardMedia
				className="w-24 h-18"
				component="img"
				alt="圖片尚未準備"
				image={dish.image}
			/>
			<CardContent className=" bg-slate-300">
				<Typography gutterBottom variant="h6" component="div">
				{dish.name}
				</Typography>
				<Typography variant="body2" color="text.secondary">
				{dish.description}
				</Typography>
			</CardContent>
			{/* <CardActions className="justify-items-end">
				<Button size="small" className="justify-self-end">加到購物車</Button>
			</CardActions> */}
			</Button>
		      	<Divider variant="middle" />
		<Button size="small" className="justify-self-end">加到購物車</Button>
			</Card>
      	<Divider variant="middle" />
		<Button size="small" className="justify-self-end">加到購物車</Button>
			<Modal
			aria-labelledby="transition-modal-title"
			aria-describedby="transition-modal-description"
			open={modalOpen}
			onClose={onClick_close}
			closeAfterTransition
			BackdropComponent={Backdrop}
			BackdropProps={{
				timeout: 300,
			}}
			>
			<Fade in={modalOpen}>
			<Box sx={modalStyle}>
				<Typography id="transition-modal-title" variant="h6" component="h2">
					{dish.name}
				</Typography>
				<Typography id="transition-modal-description" sx={{ mt: 2 }}>
					{dish.description}
				</Typography>
			</Box>
			</Fade>
		</Modal>
		</div>
	)
}


export default Dish;
