import AnimateButton from "../Components/AnimateButton";
import { useState, useEffect} from "react";


const dishes = [
	{title:"義大利麵", image:"./1.jpg", type:"主食"},
	{title:"冰淇淋", image:"./2.jpg", type:"甜點"},
	{title:"白飯", image:"./3.jpg", type:"主食"},
	{title:"燙青菜", image:"./4.jpg", type:"配菜"},
	{title:"可樂", image:"./5.jpg", type:"飲料"},
	{title:"雪碧", image:"./6.jpg", type:"飲料"},
	{title:"生菜沙拉", image:"./7.jpg", type:"沙拉"},
]



const Menu = () =>{

	const [dishType, setDishType] = useState("")
	// const [allType, setAllType] = useState([])
	const [allTypeName, setAllTypeName] = useState([])
	const [allTypeImage, setAllTypeImage] = useState([])

	useEffect(() => {
		for (let i = 0; i < dishes.length; i++){
			console.log(dishes[i])
			console.log(allTypeName.includes(dishes[i].type))
			if(!allTypeName.includes(dishes[i].type)){
				setAllTypeName(all => [...all, dishes[i].type])
				setAllTypeImage([...allTypeImage, dishes[i].image])
			}
		}
		console.log(allTypeName)		
	}, []);

	const onClick_dishType = (e, type) => {
		console.log(type)
		setDishType(type)
	}

	return (
		<>
		{
		dishType === ""
		?
		allTypeName.map(
			(type, index)=><div onClick={e => onClick_dishType(e, type)}><AnimateButton imageUrl={allTypeImage[index]} imageTitle={type} imageWidth={"30%"}></AnimateButton></div>) 
		:
		<p>nothing</p>
		}

		</>
	)	
}

export default Menu;
