const NumberSelector = ({dishNum, setDishNum}) => {

	const onClick_add = () => {
		setDishNum(dishNum + 1)
	}

	const onClick_minus = () => {
		if (dishNum > 0)
			setDishNum(dishNum - 1)
	}

	return (
		<div className="inline-block space-x-4 pt-3">
			{
				dishNum <= 0
				?
				<span onClick={onClick_minus} className="border-2 pt-px pb-px pl-1.5 pr-1.5 font-bold text-lg">-</span>
				:
				<span onClick={onClick_minus} className="border-2">-</span>
			}
			<span>{dishNum}</span>
			<span onClick={onClick_add}>+</span>
		</div>

	)
}

export default NumberSelector;
